USE g2w16;

DROP TABLE IF EXISTS `TAXES`;
DROP TABLE IF EXISTS `SURVEYS`;
DROP TABLE IF EXISTS `REVIEWS`;
DROP TABLE IF EXISTS `ITEMS`;
DROP TABLE IF EXISTS `ORDERS`;
DROP TABLE IF EXISTS `USERS`;
DROP TABLE IF EXISTS `INVENTORY`;
DROP TABLE IF EXISTS `RSS`;


CREATE TABLE IF NOT EXISTS `USERS` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `email` varchar(255) NOT NULL,
  `password` varchar(20) NOT NULL,
  `fname` varchar(40) NOT NULL,
  `lname` varchar(40) NOT NULL,
  `company` varchar(50) DEFAULT NULL,
  `address1` varchar(50) NOT NULL,
  `address2` varchar(50) DEFAULT NULL,
  `city` varchar(50) NOT NULL,
  `province` char(2) NOT NULL,
  `country` varchar(50) NOT NULL,
  `postalcode` char(7) NOT NULL,
  `homephone` char(12) DEFAULT NULL,
  `cellphone` char(12) DEFAULT NULL,
  `genre` varchar(30) DEFAULT NULL,
  `isAdmin` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `email` (`email`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


CREATE TABLE IF NOT EXISTS `INVENTORY` (
  `book_id` int(11) NOT NULL AUTO_INCREMENT,
  `ISBN` varchar(13) NOT NULL,
  `title` varchar(255) NOT NULL,
  `num_pages` int(11) NOT NULL,
  `author` varchar(80) NOT NULL,
  `publisher` varchar(255) NOT NULL,
  `genre` varchar(30) NOT NULL,
  `e_format` varchar(255) NOT NULL,
  `description` text NOT NULL,
  `l_price` decimal(10,2) NOT NULL,
  `s_price` decimal(10,2) NOT NULL DEFAULT 0,
  `w_price` decimal(10,2) NOT NULL,
  `created_on` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `removal_status` tinyint(1) NOT NULL DEFAULT '0',  
  PRIMARY KEY (`book_id`),
  UNIQUE KEY `ISBN` (`ISBN`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


CREATE TABLE IF NOT EXISTS `REVIEWS` (
  `review_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `book_id` int(11) NOT NULL,
  `rate` tinyint(4) DEFAULT NULL,
  `rev_comment` text DEFAULT NULL,
  `approval_status` tinyint(1) NOT NULL DEFAULT '0',
  `reviewed_on` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`review_id`),
  CONSTRAINT fk_reviews_user_id FOREIGN KEY (user_id)
  REFERENCES USERS(user_id),
  CONSTRAINT fk_reviews_book_id FOREIGN KEY (book_id)
  REFERENCES INVENTORY(book_id)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


CREATE TABLE IF NOT EXISTS `ORDERS` (
  `order_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `ordered_on` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`order_id`),
  CONSTRAINT fk_orders_user_id FOREIGN KEY (user_id)
  REFERENCES USERS(user_id)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


CREATE TABLE IF NOT EXISTS `ITEMS` (
  `item_id` int(11) NOT NULL AUTO_INCREMENT,
  `order_id` int(11) NOT NULL,
  `book_id` int(11) NOT NULL,
  `price` decimal(10,2) NOT NULL,
  `pst` decimal(10,2) NOT NULL DEFAULT 0,
  `gst` decimal(10,2) NOT NULL DEFAULT 0,
  `hst` decimal(10,2) NOT NULL DEFAULT 0,
  PRIMARY KEY (`item_id`),
  CONSTRAINT fk_items_book_id FOREIGN KEY (book_id)
  REFERENCES INVENTORY(book_id),
  CONSTRAINT fk_order_id FOREIGN KEY (order_id)
  REFERENCES ORDERS(order_id) on delete cascade
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


CREATE TABLE IF NOT EXISTS `SURVEYS` (
  `survey_id` int(11) NOT NULL AUTO_INCREMENT,
  `question` varchar(255) NOT NULL,
  `choice1` varchar(30) NOT NULL,
  `votes1` int(11) NOT NULL,
  `choice2` varchar(30) NOT NULL,
  `votes2` int(11) NOT NULL,
  `choice3` varchar(30) NOT NULL,
  `votes3` int(11) NOT NULL,
  `choice4` varchar(30) NOT NULL,
  `votes4` int(11) NOT NULL,
  `choice5` varchar(30) NOT NULL,
  `votes5` int(11) NOT NULL,
  `isInEffect` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`survey_id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;


CREATE TABLE IF NOT EXISTS `TAXES` (
  `tax_id` int(11) NOT NULL AUTO_INCREMENT,
  `province` varchar(2) NOT NULL,
  `pst` decimal(10,2) NOT NULL DEFAULT 0,
  `gst` decimal(10,2) NOT NULL DEFAULT 0,
  `hst` decimal(10,2) NOT NULL DEFAULT 0,
  PRIMARY KEY (`tax_id`)
)ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `RSS` (
  `rss_id` int(11) NOT NULL AUTO_INCREMENT,
  `url` varchar(255) NOT NULL,
  `source` varchar(20) NOT NULL,
  `isInEffect` tinyint(1) NOT NULL DEFAULT '0',  
  PRIMARY KEY (`rss_id`)  
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

CREATE TABLE IF NOT EXISTS `AD` (
  `ad_id` int(11) NOT NULL AUTO_INCREMENT,
  `url` varchar(255) NOT NULL,
  `source` varchar(20) NOT NULL,
  `isInEffect` tinyint(1) NOT NULL DEFAULT '0',  
  PRIMARY KEY (`ad_id`)  
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;









